{"filter":false,"title":"gpt.py","tooltip":"/gpt/gpt.py","undoManager":{"mark":0,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":51,"column":7},"action":"insert","lines":["<!DOCTYPE html>","<html>","<head>","    <title>BMI Calculator</title>","    <style>","        body {","            font-family: Arial, sans-serif;","            text-align: center;","        }","    </style>","</head>","<body>","    <h1>BMI Calculator</h1>","    <label for=\"weight\">Weight (kg):</label>","    <input type=\"number\" id=\"weight\" step=\"0.01\">","    <br>","    <label for=\"height\">Height (cm):</label>","    <input type=\"number\" id=\"height\" step=\"0.01\">","    <br>","    <button onclick=\"calculateBMI()\">Calculate BMI</button>","    <br>","    <div id=\"result\"></div>","","    <script>","        function calculateBMI() {","            var weight = parseFloat(document.getElementById(\"weight\").value);","            var height = parseFloat(document.getElementById(\"height\").value) / 100; // Convert height to meters","","            if (isNaN(weight) || isNaN(height) || height === 0) {","                document.getElementById(\"result\").innerHTML = \"Please enter valid weight and height.\";","                return;","            }","","            var bmi = weight / (height * height);","","            var result = \"Your BMI is \" + bmi.toFixed(2);","","            if (bmi < 18.5) {","                result += \" - Underweight\";","            } else if (bmi < 25) {","                result += \" - Normal weight\";","            } else if (bmi < 30) {","                result += \" - Overweight\";","            } else {","                result += \" - Obesity\";","            }","","            document.getElementById(\"result\").innerHTML = result;","        }","    </script>","</body>","</html>"],"id":1}]]},"ace":{"folds":[],"scrolltop":420,"scrollleft":0,"selection":{"start":{"row":16,"column":44},"end":{"row":16,"column":44},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":22,"state":"start","mode":"ace/mode/python"}},"timestamp":1688370439276,"hash":"424385939e1145bc95915c1ba22ef78bd42c545a"}